import{_ as t}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as d,c as r,a as h}from"./app.6c1a7a4b.js";const e={},a=h('<h1 id="mysql索引类型总结" tabindex="-1"><a class="header-anchor" href="#mysql索引类型总结" aria-hidden="true">#</a> MySQL索引类型总结</h1><h2 id="从数据结构角度分类" tabindex="-1"><a class="header-anchor" href="#从数据结构角度分类" aria-hidden="true">#</a> 从数据结构角度分类</h2><table><thead><tr><th>索引类型</th><th>特点</th><th>适用场景</th><th>常用引擎</th></tr></thead><tbody><tr><td>B+树索引</td><td>通过树形结构存储数据，支持有序数据的快速查找</td><td>范围查询(BETWEEN)和精确查询(=)，排序和聚合操作</td><td>InnoDB, MyISAM (MySQL默认索引类型)</td></tr><tr><td>哈希索引</td><td>基于哈希表结构，查询速度非常快</td><td>等值查询(=)，不支持范围查询(&gt;、&lt;)</td><td>Memory引擎</td></tr><tr><td>倒排索引(全文索引)</td><td>将全文分词，存储词与文档的映射</td><td>模糊匹配和关键字搜索，适合大文本字段(TEXT类型)</td><td>用于全文搜索</td></tr><tr><td>R-树索引</td><td>为多维空间数据设计</td><td>空间查询(地理位置最近距离、区域查询)</td><td>用于GIS地理信息系统</td></tr></tbody></table><h2 id="从innodb-b-树索引角度分类" tabindex="-1"><a class="header-anchor" href="#从innodb-b-树索引角度分类" aria-hidden="true">#</a> 从InnoDB B+树索引角度分类</h2><table><thead><tr><th>索引类型</th><th>特点</th><th>存储内容</th><th>数量限制</th></tr></thead><tbody><tr><td>聚簇索引</td><td>基于主键排序存储</td><td>叶子节点存储完整数据行</td><td>每表只能有一个(主键索引)</td></tr><tr><td>非聚簇索引</td><td>又称二级索引或辅助索引</td><td>叶子节点仅保存索引字段和主键值</td><td>一个表可有多个，查询完整数据需回表</td></tr></tbody></table><h2 id="从索引性质角度分类" tabindex="-1"><a class="header-anchor" href="#从索引性质角度分类" aria-hidden="true">#</a> 从索引性质角度分类</h2><table><thead><tr><th>索引类型</th><th>特点</th><th>使用场景</th><th>限制条件</th></tr></thead><tbody><tr><td>普通索引</td><td>非主键索引且非唯一索引</td><td>提高常规查询性能</td><td>无特殊限制</td></tr><tr><td>主键索引</td><td>表中每行数据唯一标识</td><td>唯一标识记录，提高查询效率</td><td>每表只能有一个，值不能为NULL</td></tr><tr><td>联合索引</td><td>由多个列组成，按指定顺序排列</td><td>多条件查询</td><td>需考虑最左前缀匹配原则</td></tr><tr><td>唯一索引</td><td>保证索引列值唯一</td><td>防止重复数据插入</td><td>允许NULL值，但一列中只能有一个NULL</td></tr><tr><td>全文索引</td><td>支持对长文本字段的关键字查找</td><td>文本内容复杂搜索</td><td>适用于TEXT类型字段</td></tr><tr><td>空间索引</td><td>用于空间数据查询</td><td>地图经纬度坐标等多维数据查询</td><td>主要用于MyISAM和InnoDB的地理信息数据</td></tr></tbody></table>',7),n=[a];function o(i,c){return d(),r("div",null,n)}const l=t(e,[["render",o],["__file","MySQL 的索引类型有哪些.html.vue"]]);export{l as default};
